pub struct ConfigurationAggregatorStatusAtProviderAccountAggregationSource {
    /// List of 12-digit account IDs of the account(s) being aggregated.
    #[serde(default, skip_serializing_if = "Option::is_none", rename = "accountIds")]
    pub account_ids: Option<Vec<String>>,
    /// If true, aggregate existing AWS Config regions and future regions.
    #[serde(default, skip_serializing_if = "Option::is_none", rename = "allRegions")]
    pub all_regions: Option<bool>,
    /// List of source regions being aggregated.
    #[serde(default, skip_serializing_if = "Option::is_none")]
    pub regions: Option<Vec<String>>,
}

/// The organization to aggregate config data from as documented below.
